<div id='app'>
   <div v-if='loading == false' class='page-product-water' :class='sortFeatureOpen == true ? "add-overlay" : ""'>
      <div class='appbar style01'>
         <div class='appbar-top'>
            <div class='leading'>
               <button @click='goBack' class='btn-action'>
                  <svg width="11" height="16" viewBox="0 0 11 16" fill="none" xmlns="http://www.w3.org/2000/svg">
                  <path fill-rule="evenodd" clip-rule="evenodd" d="M10.5309 0.375342C10.8759 0.806604 10.806 1.4359 10.3747 1.78091L2.60078 8.00004L10.3747 14.2192C10.806 14.5642 10.8759 15.1935 10.5309 15.6247C10.1859 16.056 9.55657 16.1259 9.12531 15.7809L0.375305 8.78091C0.13809 8.59113 0 8.30382 0 8.00004C0 7.69625 0.13809 7.40894 0.375305 7.21917L9.12531 0.219168C9.55657 -0.125842 10.1859 -0.0559202 10.5309 0.375342Z" fill="#252831"/>
                  </svg>
               </button>
               <p class='leading-title'><?php echo __('Water', 'watergo'); ?></p>
            </div>
            <div class='action'>
               <div @click='buttonSortFeature' class='btn-text'>
                  <svg width="16" height="14" viewBox="0 0 16 14" fill="none" xmlns="http://www.w3.org/2000/svg">
                  <path d="M1.50197e-05 1.08889V0.252778V1.08889ZM1.50197e-05 1.08889C-0.000569119 1.17459 0.0158907 1.25955 0.0484373 1.33883M1.50197e-05 1.08889L0.0484373 1.33883M0.0484373 1.33883C0.0809839 1.41811 0.128968 1.49013 0.189598 1.55069M0.0484373 1.33883L0.189598 1.55069M0.189598 1.55069L6.02293 7.38403L0.189598 1.55069ZM6.55326 6.8537L0.750015 1.05045V0.75H14.8056V1.05531L9.01691 6.84398L8.79724 7.06365V7.37431V12.7887L6.77293 11.7808V7.38403V7.07337L6.55326 6.8537ZM0.719656 1.02009C0.719747 1.02018 0.719838 1.02027 0.719929 1.02036L0.719656 1.02009Z" fill="#2790F9" stroke="#2790F9" stroke-width="1.5"/>
                  </svg>
                  <span class='text'><?php echo __('Sort', 'watergo'); ?></span>
               </div>
            </div>
         </div>

         <div class='appbar-bottom'>
            <div v-if='sortFeatureOpen == true' class='box-sort' :class='sortFeatureOpen == true ? "active" : ""'>
               <ul>
                  <li @click='buttonSortFeatureSelected(0)' :class='sortFeatureCurrentValue == 0 ? "active" : ""'><?php echo __('Nearest', 'watergo'); ?></li>
                  <li @click='buttonSortFeatureSelected(1)' :class='sortFeatureCurrentValue == 1 ? "active" : ""'><?php echo __('Cheapest', 'watergo'); ?></li>
                  <li @click='buttonSortFeatureSelected(2)' :class='sortFeatureCurrentValue == 2 ? "active" : ""'><?php echo __('Top Rated', 'watergo'); ?></li>
               </ul>
            </div>

            <ul class='navbar auto-resize-375 navbar-icon'>
               <li @click='select_category(cat.id)' 
                  v-for='(cat, index) in categoryWater' :key='index' 
                  :class='cat.active == true ? "active" : ""'>
                  <span class='icon' v-html='cat.icon'></span>
                  <span class='text'>{{ cat.name }}</span>
               </li>
            </ul>
            <ul class='navbar brand-resize style01 '>
               <li @click='select_brand(brand.id)' 
                  v-for='(brand, index) in brandWater' :key='index'
                  :class='brand.active == true ? "active" : ""'>
                  {{ brand.name }}
               </li>
            </ul>

         </div>
      </div>

      <div class='inner'>
         <div class='scaffold'>
            <div v-show='loading_data == false' class='grid-masonry'>
               <div 
                  @click='gotoProductDetail(product.id)' 
                  class='product-design' 
                  v-for='(product, index) in products ' :key='index'
                  :class='product.product_image.dummy != undefined ? "img-dummy" : "" '
               >
                  <div class='img'>
                     <img :src='product.product_image.url'>
                     <span v-if='has_discount(product) == true' class='badge-discount'>-{{ product.discount_percent }}%</span>
                  </div>
                  <div class='box-wrapper'>
                     <p class='tt01'>{{ product.name }} </p>
                     <p class='tt02'>{{ product.name_second }}</p>

                     <div class='gr-price' :class="has_discount(product) == true ? 'has_discount' : '' ">
                        <span class='price'>
                           {{ common_price_after_discount(product ) }}
                        </span>
                        <span v-if='has_discount(product) == true' class='price-sub'>
                           {{ common_price_show_currency(product.price) }}
                        </span>
                     </div>

                  </div>
               </div>
            </div>

            <div v-show='loading_data == true' class='progress-center'>
               <div class='progress-container enabled'><progress class='progress-circular enabled' ></progress></div>
            </div>
            
         </div>
      </div>
   </div>

   <div v-if='loading == true'>
      <div class='progress-center'>
         <div class='progress-container enabled'><progress class='progress-circular enabled' ></progress></div>
      </div>
   </div>

</div>

<script type='module'>

var { createApp } = Vue;

createApp({
   data(){
      return{ 
         loading: false,
         sortFeatureOpen: false,
         sortFeatureCurrentValue: -1,
         latitude: 10.780900239854994,
         longitude: 106.7226271387539,
         paged: 0,

         products: [],
         categoryWater: [],
         brandWater: [],

         arg_get_product: 0,

         loading_data: false,
         
      }
   },

   methods: {

      get_current_location(){

         if( window.appBridge !== undefined ){
            window.appBridge.getLocation().then( (data) => {
               if (Object.keys(data).length === 0) {
                  // alert("Error-1 :Không thể truy cập vị trí");
               }else{
                  let lat = data.lat;
                  let lng = data.lng;
                  this.latitude = data.lat;
                  this.longitude = data.lng;
               }
            }).catch((e) => { })
         }
      },

      has_discount( product ){ return window.has_discount(product); },
      common_price_show_currency(p){ return window.common_price_show_currency(p) },
      common_price_after_discount(p){ return window.common_price_after_discount(p) },

      buttonSortFeatureSelected( index ){
         this.sortFeatureCurrentValue = index;
         this.sortFeatureOpen = false;
         window.bodyScrollToggle('remove');
      },

      buttonSortFeature(){
         this.sortFeatureOpen = !this.sortFeatureOpen;
         window.bodyScrollToggle();
      },

      select_category(cat_id){
         this.categoryWater.some( cat => { 
            if (cat.id === cat_id) {
               cat.active = !cat.active;
            } else {
               cat.active = false;
            }
         });
      },
      select_brand(brand_id){
         this.brandWater.some( brand => { 
            if (brand.id === brand_id) {
               brand.active = !brand.active;
            } else {
               brand.active = false; 
            }
         });
      },
      
      async load_category(){
         var form = new FormData();
         form.append('action', 'atlantis_load_category');
         form.append('category', 'water_category');
         var r = await window.request(form);
         if( r != undefined ){
            var res = JSON.parse( JSON.stringify(r));
            if( res.message == 'category_found' ){
               res.data.some( (item, index) => item.active = false);
               res.data.forEach( item => {
                  if( item.id == 5 ){
                     item.icon = `
                        <svg width="37" height="37" viewBox="0 0 37 37" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
                        <rect width="37" height="37" fill="url(#pattern0)"/>
                        <defs>
                        <pattern id="pattern0" patternContentUnits="objectBoundingBox" width="1" height="1">
                        <use xlink:href="#image0_2645_189" transform="scale(0.00195312)"/>
                        </pattern>
                        <image id="image0_2645_189" width="512" height="512" xlink:href="data:image/png;base64,"/>
                        </defs>
                        </svg>
                     `;

                  }
                  if( item.id == 6 ){
                     item.icon = `
                        <svg width="37" height="37" viewBox="0 0 37 37" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
                        <rect x="37" y="37" width="37" height="37" transform="rotate(-180 37 37)" fill="url(#pattern1)"/>
                        <defs>
                        <pattern id="pattern1" patternContentUnits="objectBoundingBox" width="1" height="1">
                        <use xlink:href="#image0_2645_192" transform="scale(0.00195312)"/>
                        </pattern>
                        <image id="image0_2645_192" width="512" height="512" xlink:href="data:image/png;base64,"/>
                        </defs>
                        </svg>
                     `;
                  }
                  if( item.id == 7 ){
                     item.icon = `
                        <svg width="37" height="37" viewBox="0 0 37 37" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
                        <rect width="37" height="37" fill="url(#pattern2)"/>
                        <defs>
                        <pattern id="pattern2" patternContentUnits="objectBoundingBox" width="1" height="1">
                        <use xlink:href="#image0_2649_353" transform="scale(0.00195312)"/>
                        </pattern>
                        <image id="image0_2649_353" width="512" height="512" xlink:href="data:image/png;base64,"/>
                        </defs>
                        </svg>
                     `;
                  }
                  if( item.id == 8 ){
                     item.icon = `
                        <svg width="17" height="37" viewBox="0 0 17 37" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
                        <rect width="16.9143" height="37" fill="url(#pattern3)"/>
                        <defs>
                        <pattern id="pattern3" patternContentUnits="objectBoundingBox" width="1" height="1">
                        <use xlink:href="#image0_2645_186" transform="matrix(0.00427246 0 0 0.00195312 -0.59375 0)"/>
                        </pattern>
                        <image id="image0_2645_186" width="512" height="512" xlink:href="data:image/png;base64,"/>
                        </defs>
                        </svg>
                     `;
                  }
               });
               this.categoryWater.push( ...res.data);
            }
         }
      },

      async load_brand(){
         var form = new FormData();
         form.append('action', 'atlantis_load_category');
         form.append('category', 'water_brand');
         var r = await window.request(form);
         if( r != undefined ){
            var res = JSON.parse( JSON.stringify(r));
            if( res.message == 'category_found' ){
               res.data.some( (item, index) => item.active = false );
               this.brandWater.push( ...res.data);
            }
         }
      },

      async handleScroll() {
         const windowTop            = window.pageYOffset || document.documentElement.scrollTop;
         const scrollEndThreshold   = 50; // Adjust this value as needed
         const scrollPosition       = window.pageYOffset || document.documentElement.scrollTop;
         const windowHeight         = window.innerHeight;
         const documentHeight       = document.documentElement.scrollHeight;
         var windowScroll     = scrollPosition + windowHeight + scrollEndThreshold;
         var documentScroll   = documentHeight + scrollEndThreshold;

         var category_id = 0;
         var brand_id = 0;

         var _is_filter = this.get_filter_cat_brand();
         if( _is_filter ){
            category_id = _is_filter.category_id;
            brand_id = _is_filter.brand_id;
         }

         if (scrollPosition + windowHeight + 10 >= documentHeight - 10) {
            this.sortFeatureCurrentValue = -1;
            await this.load_product_sort( this.paged++, category_id, brand_id );
         }
      },


      goBack(){ window.goBack(); },
      gotoProductDetail(product_id){ window.gotoProductDetail(product_id)},

      async load_product_sort( paged, category, brand ){
         var form = new FormData();
         form.append('action', 'atlantis_get_product_sort_version2');
         form.append('product_type', 'water');
         form.append('paged', paged);
         if(category != undefined && category != 0 ){
            form.append('category', category);
         }
         if(brand != undefined && brand != 0 ){
            form.append('brand', brand);
         }
         form.append('lat', this.latitude);
         form.append('lng', this.longitude);
         var r = await window.request(form);
         if( r != undefined ){
            var res = JSON.parse( JSON.stringify(r));
            if( res.message == 'product_found' ){
               // this.products.push( ...res.data);
               res.data.forEach(item => {
                  if (!this.products.some(existingItem => existingItem.id === item.id)) {
                     this.products.push(item);
                  }
               });
            }
         }
      },

      get_filter_cat_brand(){
         var cat = this.categoryWater.find(c => c.active == true);
         var brand = this.brandWater.find(b => b.active == true);
         if (cat && brand) {
            return { category_id: cat.id, brand_id: brand.id };
         } else if(cat) {
            return { category_id: cat.id, brand_id: 0 };
         } else if(brand) {
            return { brand_id: brand.id, category_id: 0 };
         } else{
            return false;
         }
      }

   },

   mounted() {
      window.addEventListener('scroll', this.handleScroll);
   },
   beforeDestroy() {
      window.removeEventListener('scroll', this.handleScroll);
   },

   // STREAM 
   watch: {

      /**
       * @access DEEP SEARCH
       */
      brandWater: {
         async handler( data ) {
            this.loading_data = true;
            var _is_filter = this.get_filter_cat_brand();
            if( _is_filter ){
               this.sortFeatureCurrentValue = -1;
               this.products = [];
               var category_id = _is_filter.category_id;
               var brand_id = _is_filter.brand_id;
               await this.load_product_sort(0, category_id, brand_id);
            }else{
               await this.load_product_sort(0, 0, 0);
            }
            this.loading_data = false;
         },
         deep: true
      },
      categoryWater: {
         async handler( data ){
            this.loading_data = true;
            var _is_filter = this.get_filter_cat_brand();
            if( _is_filter ){
               this.sortFeatureCurrentValue = -1;
               this.products = [];
               var category_id = _is_filter.category_id;
               var brand_id = _is_filter.brand_id;
               await this.load_product_sort(0, category_id, brand_id);
            }else{
               await this.load_product_sort(0, 0, 0);
            }
            this.loading_data = false;
         },
         deep: true
      },

      sortFeatureCurrentValue: async function( val ){

         if(val == 2 ){
            // console.log('Top Rated Filter');
            this.products.sort((a, b) => b.avg_rating - a.avg_rating);
         }
         else if(val == 1 ){
            // console.log('Top Cheapest');
            this.products.sort((a, b) => a.price - b.price);
         }
         else if(val == 0 ){
            // console.log('Nearest');
            this.products.sort((a, b) => a.distance - b.distance);
         }
         
      }
   },

   async created(){
         
      this.get_current_location();
      this.loading = true;

      await this.load_product_sort(0, 0, 0);
      await this.load_category();
      await this.load_brand();


      this.loading = false;

      window.appbar_fixed();


   },

}).mount('#app');

</script>